    const addItemToCartHandler = (item) => {
        dispatchCartAction({ type: 'ADD_ITEM_TO_CART', item: item })
    };

    const removeItemFromCartHandler = (id) => {
        dispatchCartAction({ type: 'REMOVE_ITEM_FROM_CART', id: id })
    };

