import { useEffect } from "react";
import NoQuotesFound from "../components/quotes/NoQuotesFound";
import QuoteList from "../components/quotes/QuoteList";
import LoadingSpinner from "../components/UI/LoadingSpinner";
import useHttp from "../hooks/use-http";
import { getAllQuotes } from "../lib/api";

const DUMMY_QUOTES = [
    { id: 'q1', author: 'Max', text: 'Learning React is fun 01!' },
    { id: 'q2', author: 'Pedro', text: 'Learning React is fun 02!' },
    { id: 'q3', author: 'Jerónimo', text: 'Learning React is fun 03!' },
];

const AllQuotes = () => {
    const { sendRequest, status, data: loadedQuotes, error } = useHttp(getAllQuotes, true);

    useEffect(() => {
        sendRequest()
    }, [sendRequest]);

    if (status === 'pending') {
        return (
            <div className="centered">
                <LoadingSpinner quotes={loadedQuotes} />
            </div>
        )
    }

    if (error) {
        return (
            <p className="centered focused">
                {error}
            </p>
        )
    }

    if (status === 'completed' && (!loadedQuotes || loadedQuotes.length === 0)) {
        return <NoQuotesFound />
    }

    return (
        <>
            <QuoteList quotes={DUMMY_QUOTES} />
        </>
    )
}
export default AllQuotes;
